DROP TABLE IF EXISTS PEOPLE_LOCATION ; 
DROP TABLE IF EXISTS PEOPLE_SPECIES;
DROP TABLE IF EXISTS PEOPLE;
DROP TABLE IF EXISTS ANIMAL;
DROP TABLE IF EXISTS SPECIES; 
DROP TABLE IF EXISTS LOCATION ; 


CREATE TABLE PEOPLE (
	PEOPLE_ID int(11) NOT NULL AUTO_INCREMENT,
	FIRST_NAME varchar(255) NOT NULL,
	LAST_NAME varchar(255) NOT NULL,
	PRIMARY KEY (PEOPLE_ID)	,
	UNIQUE KEY people_name_key (FIRST_NAME, LAST_NAME)
);

CREATE TABLE SPECIES (
	SPECIES_ID int(11) NOT NULL AUTO_INCREMENT,
	SPECIES_NAME varchar(255) NOT NULL,
	PRIMARY KEY (SPECIES_ID),
	UNIQUE(SPECIES_NAME)
);

CREATE TABLE LOCATION (
	LOCATION_ID int(11) NOT NULL AUTO_INCREMENT,
	ZIP_CODE char(5) NOT NULL, 
	CITY varchar(255) NOT NULL, 
	STATE char(2) NOT NULL, 
	PRIMARY KEY (LOCATION_ID)
);

CREATE TABLE ANIMAL (
	ANIMAL_ID int(11) NOT NULL AUTO_INCREMENT, 
	NAME varchar(255) NOT NULL, 
	AGE int(11) NOT NULL,
	WEIGHT int(11) NOT NULL, 
	LOCATION_ID int(11), 
	SPECIES_ID int(11), 
	PRIMARY KEY(ANIMAL_ID),
	FOREIGN KEY (LOCATION_ID) REFERENCES LOCATION(LOCATION_ID) ON DELETE SET NULL, 
	FOREIGN KEY (SPECIES_ID) REFERENCES SPECIES(SPECIES_ID) ON DELETE SET NULL
);

CREATE TABLE PEOPLE_LOCATION (
	PEOPLE_LOCATION_ID int(11) NOT NULL AUTO_INCREMENT,
	PEOPLE_ID int(11),
	LOCATION_ID int(11), 
	PRIMARY KEY (PEOPLE_LOCATION_ID),
	FOREIGN KEY (PEOPLE_ID) REFERENCES PEOPLE(PEOPLE_ID) ON DELETE CASCADE,
	FOREIGN KEY (LOCATION_ID) REFERENCES LOCATION(LOCATION_ID) ON DELETE CASCADE,
	UNIQUE people_location_uniq(PEOPLE_ID, LOCATION_ID)
);


CREATE TABLE PEOPLE_SPECIES(
	PEOPLE_SPECIES_ID int(11) NOT NULL AUTO_INCREMENT, 
	PEOPLE_ID int(11),
	SPECIES_ID int(11), 
	PRIMARY KEY (PEOPLE_SPECIES_ID), 
	FOREIGN KEY (PEOPLE_ID) REFERENCES PEOPLE(PEOPLE_ID) ON DELETE CASCADE,
	FOREIGN KEY (SPECIES_ID) REFERENCES SPECIES(SPECIES_ID) ON DELETE CASCADE,
	UNIQUE people_species_uniq(PEOPLE_ID, SPECIES_ID)
);


